using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using NUnit.Framework;
using NUnit.Framework.Legacy;
using Bio.IO.PacBio;

namespace Bio.Tests.PacBio
{
    [TestFixture]
    public static class ParserTests
    {
        [Test]
        [Category("PacBio")]
        public static void SimpleCCSTest ()
        {
            string fname = System.IO.Path.Combine ("TestUtils", "PacBio", "ccs.bam").TestDir();
            var csp = new PacBioCCSBamReader ();
            var seqs = csp.Parse (fname).Select(z => z as PacBioCCSRead).ToList();

            var seq4 = seqs [4];
            ClassicAssert.AreEqual (273, seq4.HoleNumber);
            ClassicAssert.AreEqual (12, seq4.NumPasses);
            ClassicAssert.AreEqual (1, seq4.ReadCountBadZscore);
            ClassicAssert.AreEqual (1946, seq4.Sequence.Count);
            ClassicAssert.AreEqual (14, seq4.ZScores.Length);
            ClassicAssert.AreEqual("m150930_045019_42194_c100916310150000001823201204291662_s1_p0/273/ccs",
                             seq4.Sequence.ID);
            var seq = new Sequence (DnaAlphabet.Instance, seq4.Sequence.ToArray (), true);
            ClassicAssert.AreEqual("",
                seq.ConvertToString());

            ClassicAssert.AreEqual (seqs.Count, 7);
        }
    }
}

